use crate::shapes::{FileInput, FileOutput, GlobInput, GlobOutput, Uri};

fn create_uri(prefix: &str, path: impl AsRef<str>) -> Uri {
    let protocol = format!("{prefix}://");
    let path = path.as_ref();

    Uri::parse(if path.starts_with(&protocol) {
        path.to_owned()
    } else {
        format!(
            "{protocol}{}",
            if prefix == "glob" {
                path.replace("?", "__QM__")
            } else {
                path.to_owned()
            }
        )
    })

