use clap::ValueEnum;
use std::fmt;

#[derive(ValueEnum, Clone, Debug, Default)]
pub enum AppTheme {
    #[default]
    Dark,
    Light,
}

impl fmt::Display for AppTheme {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> Result<(), fmt::Error> {
        write!(
            f,
            "{}",
            match self {
                AppTheme::Dark => "dark",
                AppTheme::Light => "light",
            }
        )?;

        Ok(())
    }
}

